mapper.select:
SELECT I.ID, IDEA_TITLE, IDEA_INTRO, IDEA_DETAIL, IDEA_CATEGORY, IDEA_REGISTER_DATE, IDEA_UPDATE_DATE, IDEA_TYPE, IDEA_PRICE, IDEA_READ_COUNT, IDEA_MAX, I.USER_ID, I.USER_NICKNAME, COALESCE(IDEA_SCRAP_COUNT, 0) AS IDEA_SCRAP_COUNT FROM ( SELECT ROWNUM R, I2.* FROM ( SELECT I1.ID, IDEA_TITLE, IDEA_INTRO, IDEA_DETAIL, IDEA_CATEGORY, IDEA_REGISTER_DATE, IDEA_UPDATE_DATE, IDEA_TYPE, IDEA_PRICE, IDEA_READ_COUNT, IDEA_MAX, I1.USER_ID, U.USER_NICKNAME, COUNT(S.ID) AS IDEA_SCRAP_COUNT FROM TBL_USER U INNER JOIN TBL_IDEA I1 ON U.ID = I1.USER_ID LEFT JOIN TBL_SCRAP S ON I1.ID = S.IDEA_ID WHERE 1=1
AND IDEA_CATEGORY = #{search.ideaCategory}
AND IDEA_PRICE >= 100000
AND IDEA_PRICE < 10000
AND (IDEA_PRICE >= #{search.ideaPrice} * 10000 AND IDEA_PRICE < (#{search.ideaPrice} + 1) * 10000 )
AND IDEA_MAX = #{search.ideaMax}
GROUP BY I1.ID, IDEA_TITLE, IDEA_INTRO, IDEA_DETAIL, IDEA_CATEGORY, IDEA_REGISTER_DATE, IDEA_UPDATE_DATE, IDEA_TYPE, IDEA_PRICE, IDEA_READ_COUNT, IDEA_MAX, I1.USER_ID, U.USER_NICKNAME ORDER BY
IDEA_READ_COUNT DESC
IDEA_SCRAP_COUNT DESC
I1.ID DESC
) I2 WHERE ROWNUM <= #{pagination.page} * #{pagination.rowCount} ) I WHERE R > (#{pagination.page} - 1) * #{pagination.rowCount}

SELECT COUNT(I.ID) FROM TBL_USER U JOIN TBL_IDEA I ON U.ID = I.USER_ID WHERE 1=1
AND IDEA_CATEGORY = #{search.ideaCategory}
AND IDEA_PRICE >= 100000
AND (IDEA_PRICE >= #{search.ideaPrice} * 10000 AND IDEA_PRICE < (#{search.ideaPrice} + 1) * 10000 )
AND IDEA_MAX = #{search.ideaMax}

mapper.update:
UPDATE TBL_IDEA
SET IDEA_READ_COUNT = IDEA_READ_COUNT + 1
WHERE ID = #{id}
